import React, { useState } from 'react';
import Layout from '../../components/layout';

function Register(props) {
  const [user, setUser] = useState({
    name: '',
    email: '',
    password: '',
    password2: '',
  });

  const { name, email, password, password2 } = user;

  const onChange = (e) => setUser({ ...user, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();
    if (name === '' || email === '' || password === '') {
      alert('You must provide a valid username and password');
    } else {
      const register = { name, email, password };
    }
  };

  return (
    <Layout>
      <div className='row align-items-center'>
        <div className='col-lg-7 py-3 py-md-5'>
          <h1 className='display-2'>Modernization CMS Platform</h1>
          <p className='lead text-muted'>
            Steadfast removes road blocks for sales teams by providing a
            cohesive platform that allows for online growth using modern,
            scalable and secure CMS application for business.
          </p>
        </div>
        <div className='col-lg-5 pl-lg-5 pb-3 py-lg-5'>
          <div className='form-group'>
            <div className='form-container'>
              <h1>
                Account <span className='text-primary'>Register</span>
              </h1>
              <form onSubmit={onSubmit}>
                <div className='form-group'>
                  <label htmlFor='name'>Name</label>
                  <input
                    type='text'
                    name='name'
                    value={name}
                    onChange={onChange}
                    autoComplete='Off'
                  />
                </div>
                <div className='form-group'>
                  <label htmlFor='email'>Email Address</label>
                  <input
                    type='text'
                    name='email'
                    value={email}
                    onChange={onChange}
                    autoComplete='Off'
                  />
                </div>
                <div className='form-group'>
                  <label htmlFor='password'>Password</label>
                  <input
                    type='password'
                    name='password'
                    value={password}
                    onChange={onChange}
                    minLength='6'
                    autoComplete='Off'
                  />
                </div>
                <div className='form-group'>
                  <label htmlFor='password2'>Confirm Password</label>
                  <input
                    type='password'
                    name='password2'
                    value={password2}
                    onChange={onChange}
                    minLength='6'
                    autoComplete='Off'
                  />
                </div>
                <input
                  type='submit'
                  value='Register'
                  className='btn btn-primary btn-block'
                />
              </form>
            </div>
          </div>
        </div>
      </div>
    </Layout>
  );
}

export default Register;
